#include <iostream>
#include "DtoOther.h"
using namespace std;
DtoOther::DtoOther(int dn,int ns){	decimalNumber = dn;	numberSystem = ns;}void DtoOther::convert(){	stack<int> stk;	if(decimalNumber == 0){		cout << 0 << endl;		return ;	}	while(decimalNumber){		stk.push(decimalNumber % numberSystem);		decimalNumber /= numberSystem;	}	switch (numberSystem){		case 16:cout <<"十六进制为："<< "0x";break;		case 8:cout << "八进制为："<< "0";break;		case 2:cout << "二进制为：";break;		default:	cout << numberSystem <<"进制为：";break;	}	while(! stk.empty() ){		switch(stk.top()){			case 10:cout << "A";break;			case 11:cout << "B";break;			case 12:cout << "C";break;			case 13:cout << "D";break;				case 14:cout << "E";break;			case 15:cout << "F";break;			default:cout << stk.top();break;		}			stk.pop();	}	cout << endl;}